INSERT INTO `faq` (`faq_id`, `user_id`, `admin_id`, `faq_tags`, `faq_title`, `faq_content`, `faq_password`, `faq_response`, `faq_response_time`, `faq_status`, `created_at`) VALUES
	(1, 1, NULL, '사이트이용', '어떻게 도서관 온라인 홈페이지를 이용할 수 있나요?', '도서관 온라인 홈페이지를 이용하려면 먼저 회원가입을 완료해야 합니다. 가입 후 로그인하여 도서 대출, 반납, 예약 등 다양한 서비스를 이용하실 수 있습니다. 자세한 이용 안내는 [사용자 매뉴얼 링크]에서 확인하실 수 있습니다.', 'qwer1234!', NULL, NULL, 0, '2024-01-24 14:47:11'),
	(2, 1, NULL, '계정', '비밀번호를 분실했을 때 어떻게 찾을 수 있나요?', '비밀번호 분실 시, 로그인 화면에서 \'비밀번호 찾기\' 링크를 클릭하여 가입 시 등록한 이메일 주소를 입력하시면 비밀번호 재설정 링크를 받을 수 있습니다. 이메일로 받은 안내에 따라 비밀번호를 변경하세요.', 'qwer1234!', NULL, NULL, 0, '2024-01-24 14:47:11'),
	(3, 1, NULL, '대출', '도서 대출 기간을 연장하려면 어떻게 해야 하나요?', '도서 대출 기간 연장은 로그인 후 \'대출 내역\'에서 가능합니다. 연장이 허용되는 횟수 및 조건은 도서당 다를 수 있으며, 연장 기간은 기존 대출 기간에 추가됩니다. 연장이 불가능한 도서는 미리 반납 후 재대출이 필요합니다.', 'qwer1234!', NULL, NULL, 0, '2024-01-24 14:47:11'),
	(4, 1, NULL, '도서', '희망 도서가 대출 중일 때 예약할 수 있나요?', '네, 대출 중인 도서는 예약이 가능합니다. 대출 중인 도서가 반납되면 예약자에게 순차적으로 통보됩니다. 예약한 도서는 알림 메시지를 확인하여 일정 기간 내에 대출하셔야 합니다.', 'qwer1234!', NULL, NULL, 0, '2024-01-24 14:47:11'),
	(5, 1, NULL, '계정', '비밀번호를 까먹었어요', '어떻게하나요....', 'ㅇㅇ', NULL, NULL, 0, '2024-01-25 17:01:27');
