-- library 데이터베이스 구조 내보내기
CREATE DATABASE IF NOT EXISTS `library` /*!40100 DEFAULT CHARACTER SET latin1 */;
USE `library`;

-- 테이블 library.admin 구조 내보내기
CREATE TABLE IF NOT EXISTS `admin` (
  `admin_id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID (기본키)',
  `admin_email` varchar(255) NOT NULL COMMENT '이메일',
  `admin_pwd` varchar(128) NOT NULL COMMENT '비밀번호',
  `salt` varchar(200) NOT NULL COMMENT '암호화할때 쓴 난수',
  `admin_name` varchar(255) NOT NULL COMMENT '이름',
  `admin_phone` varchar(20) NOT NULL COMMENT '전화번호',
  `created_at` datetime NOT NULL COMMENT '가입일',
  PRIMARY KEY (`admin_id`),
  UNIQUE KEY `admin_email` (`admin_email`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- 테이블 데이터 library.admin:~1 rows (대략적) 내보내기

-- 테이블 library.banner 구조 내보내기
CREATE TABLE IF NOT EXISTS `banner` (
  `banner_id` int(11) NOT NULL AUTO_INCREMENT,
  `banner_img_url` varchar(255) NOT NULL COMMENT '배너안에 들어갈 이미지 저장 경로',
  `banner_title` varchar(255) NOT NULL COMMENT '배너 제목',
  `banner_description` text NOT NULL COMMENT '배너 설명',
  `banner_adress` varchar(255) NOT NULL COMMENT '배너 경로설정',
  `createdAt` datetime NOT NULL,
  `updatedAt` datetime NOT NULL,
  `deletedAt` datetime DEFAULT NULL,
  PRIMARY KEY (`banner_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- 테이블 데이터 library.banner:~5 rows (대략적) 내보내기

-- 테이블 library.book 구조 내보내기
CREATE TABLE IF NOT EXISTS `book` (
  `book_id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'book_id',
  `book_name` varchar(255) NOT NULL COMMENT '책 제목',
  `book_img_url` varchar(255) NOT NULL COMMENT '책 표지 파일 경로',
  `book_author` varchar(255) NOT NULL COMMENT '저자',
  `book_publisher` varchar(255) NOT NULL COMMENT '출판사',
  `book_genre` varchar(255) NOT NULL COMMENT '장르',
  `book_availability` tinyint(1) NOT NULL DEFAULT 1 COMMENT '도서 대출 가능 여부',
  `book_description` text NOT NULL COMMENT '책 요약',
  `book_ISBN` varchar(255) NOT NULL COMMENT '책 고유 번호',
  `created_at` datetime NOT NULL COMMENT '작성일',
  PRIMARY KEY (`book_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- 테이블 데이터 library.book:~210 rows (대략적) 내보내기

-- 테이블 library.event 구조 내보내기
CREATE TABLE IF NOT EXISTS `event` (
  `event_id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'event_id',
  `event_title` varchar(255) NOT NULL COMMENT '이벤트 제목',
  `event_img_url` varchar(255) NOT NULL COMMENT '이벤트 썸네일 이미지 경로',
  `event_status` enum('always','ongoing','expired') DEFAULT 'ongoing' COMMENT '이벤트 상태 설정( 1 : always, 2 : ongoing, 3 : expired)',
  `event_start_date` datetime NOT NULL COMMENT '이벤트 시작일',
  `event_end_date` datetime NOT NULL COMMENT '이벤트 종료일',
  `event_max_applicants` int(11) DEFAULT NULL COMMENT '이벤트 인원 제한',
  `event_current_applicants` int(11) NOT NULL DEFAULT 0 COMMENT '현재 이벤트 신청인원',
  `read_count` int(11) NOT NULL DEFAULT 0 COMMENT '이벤트 조회 수',
  `created_at` datetime NOT NULL COMMENT '작성일',
  PRIMARY KEY (`event_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- 테이블 데이터 library.event:~12 rows (대략적) 내보내기

-- 테이블 library.event_applicants 구조 내보내기
CREATE TABLE IF NOT EXISTS `event_applicants` (
  `event_applicants_id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'event_applicants_id',
  `event_id` int(11) NOT NULL COMMENT 'event_id',
  `user_id` int(11) NOT NULL COMMENT 'user_id',
  `created_at` datetime NOT NULL COMMENT '작성일',
  PRIMARY KEY (`event_applicants_id`),
  KEY `event_id` (`event_id`),
  KEY `user_id` (`user_id`),
  CONSTRAINT `event_applicants_ibfk_1` FOREIGN KEY (`event_id`) REFERENCES `event` (`event_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `event_applicants_ibfk_2` FOREIGN KEY (`user_id`) REFERENCES `user` (`user_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- 테이블 데이터 library.event_applicants:~1 rows (대략적) 내보내기

-- 테이블 library.faq 구조 내보내기
CREATE TABLE IF NOT EXISTS `faq` (
  `faq_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `admin_id` int(11) DEFAULT NULL COMMENT 'admin테이블의 id참초',
  `faq_tags` varchar(255) NOT NULL COMMENT '문의 카테고리',
  `faq_title` varchar(255) NOT NULL COMMENT '문의 제목',
  `faq_content` text NOT NULL COMMENT '문의 내용',
  `faq_password` varchar(255) NOT NULL COMMENT '문의 비밀번호',
  `faq_response` text DEFAULT NULL COMMENT '관리자 답변 내용',
  `faq_response_time` datetime DEFAULT NULL COMMENT '답변 작성일',
  `faq_status` tinyint(1) NOT NULL DEFAULT 0 COMMENT '문의 상태 대기[0], 완료[1]',
  `created_at` datetime NOT NULL COMMENT '작성일',
  PRIMARY KEY (`faq_id`),
  KEY `user_id` (`user_id`),
  KEY `admin_id` (`admin_id`),
  CONSTRAINT `faq_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `user` (`user_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `faq_ibfk_2` FOREIGN KEY (`admin_id`) REFERENCES `admin` (`admin_id`) ON DELETE SET NULL ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- 테이블 데이터 library.faq:~5 rows (대략적) 내보내기

-- 테이블 library.loans 구조 내보내기
CREATE TABLE IF NOT EXISTS `loans` (
  `loans_id` int(11) NOT NULL AUTO_INCREMENT,
  `book_id` int(11) NOT NULL COMMENT 'book테이블의 id참초',
  `user_id` int(11) NOT NULL,
  `loan_date` datetime NOT NULL COMMENT '대출일',
  `due_date` datetime NOT NULL COMMENT '반납일',
  `createdAt` datetime NOT NULL,
  `updatedAt` datetime NOT NULL,
  `deletedAt` datetime DEFAULT NULL,
  PRIMARY KEY (`loans_id`),
  KEY `book_id` (`book_id`),
  KEY `user_id` (`user_id`),
  CONSTRAINT `loans_ibfk_1` FOREIGN KEY (`book_id`) REFERENCES `book` (`book_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `loans_ibfk_2` FOREIGN KEY (`user_id`) REFERENCES `user` (`user_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- 테이블 데이터 library.loans:~9 rows (대략적) 내보내기

-- 테이블 library.review 구조 내보내기
CREATE TABLE IF NOT EXISTS `review` (
  `review_id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'review_id',
  `book_id` int(11) NOT NULL COMMENT '책 id',
  `user_id` int(11) NOT NULL COMMENT '유저 id',
  `review_rating` int(11) NOT NULL COMMENT '별점',
  `review_title` varchar(255) NOT NULL COMMENT '리뷰 제목',
  `review_content` varchar(255) NOT NULL COMMENT '리뷰 내용',
  `created_at` datetime NOT NULL COMMENT '작성일',
  PRIMARY KEY (`review_id`),
  KEY `book_id` (`book_id`),
  KEY `user_id` (`user_id`),
  CONSTRAINT `review_ibfk_1` FOREIGN KEY (`book_id`) REFERENCES `book` (`book_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `review_ibfk_2` FOREIGN KEY (`user_id`) REFERENCES `user` (`user_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- 테이블 데이터 library.review:~1 rows (대략적) 내보내기

-- 테이블 library.user 구조 내보내기
CREATE TABLE IF NOT EXISTS `user` (
  `user_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_email` varchar(255) DEFAULT NULL COMMENT 'ID(이메일)',
  `user_pwd` varchar(128) DEFAULT NULL COMMENT '비밀번호',
  `salt` varchar(200) DEFAULT NULL COMMENT '암호화할때 쓴 난수',
  `user_name` varchar(20) DEFAULT NULL COMMENT '이름',
  `user_phone` varchar(20) DEFAULT NULL COMMENT '회원 전화번호',
  `sns_id` varchar(255) DEFAULT NULL COMMENT '소셜 로그인 아이디',
  `sns_type` varchar(255) DEFAULT NULL COMMENT '소셜 로그인 타입',
  `created_at` datetime NOT NULL COMMENT '회원 가입일',
  PRIMARY KEY (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- 테이블 데이터 library.user:~2 rows (대략적) 내보내기

